var ComponentsNoUiSliders = function() {

    var kichthuoc = function() {
        var connectSlider = document.getElementById('kichthuoc');
        

        noUiSlider.create(connectSlider, {
            start: [20],
            connect: false,
            range: {
                'min': 0,
                'max': 100
            }
        });
        
        connectSlider.noUiSlider.on('slide', function( values, handle ){
            
            var aa = values[handle];
            $("#noidungchap").css({"font-size":""+aa+"px"});
        });
    }

    

    

    return {
        //main function to initiate the module
        init: function() {
            kichthuoc();
        }

    };

}();

jQuery(document).ready(function() {    
   ComponentsNoUiSliders.init(); 
});